$(function () {
    $(document).keydown(function (_) {
        if (_.keyCode == 13) $("#login")[0].focus()
    })
});

function dologin() {
    var Uid = $("#Uid").val(),
		Pwd = $("#Pwd").val(),
		Pwdsave = $("#Pwdsave").val();
    $.ajax({
        type: "post",
        cache: false,
        dataType: "json",
        url: "handler/validate.ashx",
        data: [{
            name: "Action",
            value: "Login"
        }, {
            name: "A",
            value: Uid
        }, {
            name: "B",
            value: Pwd
        }, {
            name: "C",
            value: Pwdsave
        }],
        success: function (_) {
            if (_ == "0") window.location = "index_all.aspx";
            else if (_ == "10") window.location = "index_yb_auto.aspx";
            else if (_ == "1") window.location = "index_yb.aspx?r=" + Date.parse(new Date());
            else if (_ == "2") {
                alert("请输入有效账号!");
                $("#Uid")[0].focus();
                return
            } else if (_ == "4") {
                alert("请输入有效密码");
                $("#Pwd")[0].focus();
                return
            } else {
                alert("\u7cfb\u7edf\u9519\u8bef\uff0c\u8bf7\u8054\u7cfb\u7ba1\u7406\u5458!\uff01");
                return
            }
        },
        error: function () { },
        beforeSend: function () { },
        complete: function () { }
    })
}